import React, { Component } from "react";
import characters from "../../json";

import * as _ from "lodash";

export default class CharCard extends Component {
  state = {
    selectedChar: null,
  };

  render() {
    const buttonIcons = {
      u: "/assets/buttons/U.png",
      d: "/assets/buttons/D.png",
      f: "/assets/buttons/F.png",
      b: "/assets/buttons/B.png",
      one: "/assets/buttons/1.png",
      two: "/assets/buttons/2.png",
      three: "/assets/buttons/3.png",
      four: "/assets/buttons/4.png"
    };

    const renderComboIcon = (command) => {
      return(
        <img src={buttonIcons[command]} />
      )
    }
    const charCombos = _.get(this.state.selectedChar, "combos");

    return this.state.selectedChar ? (
      <div>
        {_.map(charCombos, (combo, index) => {
          console.log(combo);
          const combosCommands = _.get(combo, "commands");
          const combosDamage = _.get(combo, "damage");

          return (
            <>
              {_.map(combosCommands, (command) => {
                console.log(command)
                renderComboIcon(command);
              } ) }
              <h2>Dano:{combosDamage}</h2>

              <button onClick={() => this.setState({ selectedChar: null })}>
                Voltar
              </button>
            </>
          );
        })}
      </div>
    ) : (
      <div>
        {_.map(characters, (char, index) => {
          const charName = _.get(char, "name");
          return (
            <button
              onClick={() => this.setState({ selectedChar: char })}
              className="col-md-3 col-sm-6 mb-5"
            >
              <div className="card">
                <div className="card-header">
                  <img src={char.image} />
                  <h1>{charName}</h1>
                </div>
              </div>
            </button>
          );
        })}
        ;
      </div>
    );
  }
}
